// Local GSAP fallback - simplified version for basic animations
// This is a minimal implementation to prevent errors when external CDN is blocked

(function() {
    'use strict';
    
    // Basic GSAP-like object with essential methods
    window.gsap = {
        registerPlugin: function() {
            console.log('GSAP plugins registered (local fallback)');
        },
        
        to: function(target, options) {
            console.log('GSAP animation to:', target, options);
            return { duration: options.duration || 1 };
        },
        
        from: function(target, options) {
            console.log('GSAP animation from:', target, options);
            return { duration: options.duration || 1 };
        },
        
        set: function(target, options) {
            console.log('GSAP set:', target, options);
        },
        
        timeline: function(options) {
            return {
                to: this.to,
                from: this.from,
                set: this.set
            };
        }
    };
    
    // Basic plugin stubs
    window.Draggable = function() {};
    window.MotionPathPlugin = {};
    window.ScrollTrigger = {};
    
    console.log('Local GSAP fallback loaded successfully');
})();